// Proje: Gen-esis için renk ve font şeması
export const theme = {
  colors: {
    background: '#12121c', // Koyu, derin uzay laciverti
    surface: '#1e1e2f',    // Kartlar ve paneller için yüzey rengi
    primary: '#009432',    // Boğaziçi Yeşili - Ana Vurgu
    secondary: '#00FFFF',  // Siyan - Parlama ve ikonlar için
    accent: '#FF00FF',     // Macenta - Uyarılar ve özel bildirimler
    text: '#EAEAEA',       // Ana metin rengi
    textSecondary: '#A0A0B0', // İkincil, daha soluk metin
  },
  fonts: {
    headings: "'Orbitron', sans-serif",
    body: "'Poppins', sans-serif",
  },
  shadows: {
    card: '0 4px 15px rgba(0, 255, 255, 0.1)', // Hafif siyan bir parlama
  }
};
